import React, { Component } from "react";
//import the get project action to fill out the data
import { getProject, createProject } from "../../actions/projectActions";
//get the proptypes package to handle props
import PropTypes from "prop-types";
//get connect from react redux so we can wire routes
import { connect } from "react-redux";
//get the classnames  package for form validation (error fields)
import classnames from "classnames";

//rcc tab to auto create and then separate out the export
//this component has to fetch and then route to update
class UpdateProject extends Component {
  // set the state
  //create constructor
  constructor() {
    super();
    this.state = {
      id: "",
      projectIdentifier: "",
      projectName: "",
      description: "",
      startDate: "",
      endDate: "",
      errors: {}
    };
    //bind the event
    this.onChange = this.onChange.bind(this);
    //bind the submit
    this.onSubmit = this.onSubmit.bind(this);
  }

  //add life cycle hook for mounting the state
  componentWillReceiveProps(nextProps) {
    //if the parameter has the errors property
    if (nextProps.errors) {
      this.setState({ errors: nextProps.errors });
    }
    if (!nextProps.startDate) {
      // nextProps.startDate = "";

      Object.preventExtensions(nextProps);

      Object.defineProperty(nextProps, "startDate", { startDate: "" });

      // const newObj = Object.assign({ startDate: "" }, "");
      // const newnewObj = Object.assign({ endDate: "" }, "");
      //nextProps.startDate = !nextProps.startDate ? "" : nextProps.startDate;
    }
    // nextProps.endDate = !nextProps.endDate ? "" : nextProps.endDate;

    //create a const for the new props
    const {
      id,
      projectIdentifier,
      projectName,
      description,
      startDate,
      endDate
    } = nextProps.project;

    //set the current state to the new props
    this.setState({
      id,
      projectIdentifier,
      projectName,
      description,
      startDate,
      endDate
    });
    // }
  }

  //add the life cycle hook
  componentDidMount() {
    //extract the id from the params passed in through the "get" api call for fetch
    const { id } = this.props.match.params;
    //pass the id from fetch and the history into the getProject
    //now we can see our project object in the state (redux)
    this.props.getProject(id, this.props.history);
  }

  //on change event
  onChange(e) {
    //use the naming convention to wire it up
    this.setState({ [e.target.name]: e.target.value });
    //
  }
  //submit event
  onSubmit(e) {
    e.preventDefault();
    const updateProject = {
      id: this.state.id,
      projectIdentifier: this.state.projectIdentifier,
      projectName: this.state.projectName,
      description: this.state.description,
      startDate: this.state.startDate,
      endDate: this.state.endDate
    };
    //pass on to createproject function
    this.props.createProject(updateProject, this.props.history);
  }

  render() {
    //get the errors from the state
    const { errors } = this.state;

    return (
      <div className="project">
        <div className="container">
          <div className="row">
            <div className="col-md-8 m-auto">
              <h5 className="display-4 text-center">Update Project form</h5>
              <hr />
              {/* bind  */}
              <form onSubmit={this.onSubmit}>
                <div className="form-group">
                  {/* this is wired from state to value of the field for updating */}
                  <input
                    type="text"
                    className={classnames("form-control form-control-lg ", {
                      "is-invalid": errors.projectName
                    })}
                    placeholder="Project Name"
                    name="projectName"
                    value={this.state.projectName}
                    onChange={this.onChange}
                  />
                  {/* if the project name exists then create this div this is the classnames package usage syntax*/}
                  {errors.projectName && (
                    <div className="invalid-feedback">{errors.projectName}</div>
                  )}
                </div>
                <div className="form-group">
                  <input
                    type="text"
                    className={classnames("form-control form-control-lg ", {
                      "is-invalid": errors.projectIdentifier
                    })}
                    placeholder="Unique Project ID"
                    disabled
                    name="projectIdentifier"
                    value={this.state.projectIdentifier}
                  />
                  {errors.projectIdentifier && (
                    <div className="invalid-feedback">
                      {errors.projectIdentifier}
                    </div>
                  )}
                </div>
                {/*<!-- disabled for Edit Only!! remove "disabled" for the Create operation -->*/}
                <div className="form-group">
                  <textarea
                    className={classnames("form-control form-control-lg ", {
                      "is-invalid": errors.description
                    })}
                    placeholder="Project Description"
                    name="description"
                    value={this.state.description}
                    onChange={this.onChange}
                  ></textarea>
                  {errors.description && (
                    <div className="invalid-feedback">{errors.description}</div>
                  )}
                </div>
                <h6>Start Date</h6>
                <div className="form-group">
                  <input
                    type="date"
                    className={classnames("form-control form-control-lg ", {
                      "is-invalid": errors.startDate
                    })}
                    name="startDate"
                    value={this.state.startDate}
                    onChange={this.onChange}
                  />
                  {errors.startDate && (
                    <div className="invalid-feedback">{errors.startDate}</div>
                  )}
                </div>
                <h6>Estimated End Date</h6>
                <div className="form-group">
                  <input
                    type="date"
                    className={classnames("form-control form-control-lg ", {
                      "is-invalid": errors.endDate
                    })}
                    name="endDate"
                    value={this.state.endDate}
                    onChange={this.onChange}
                  />
                  {errors.endDate && (
                    <div className="invalid-feedback">{errors.endDate}</div>
                  )}
                </div>

                <input
                  type="submit"
                  className="btn btn-primary btn-block mt-4"
                />
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

//add proptypes
UpdateProject.propTypes = {
  //get project function mapped to proptype
  getProject: PropTypes.func.isRequired,
  createProject: PropTypes.func.isRequired,
  project: PropTypes.object.isRequired,
  //map the errors object to the props when addproject is going
  errors: PropTypes.object.isRequired
};

//map the state to props
const mapStateToProps = state => ({
  project: state.project.project,
  errors: state.errors
});

//connect to getProject
export default connect(
  mapStateToProps,
  { getProject, createProject }
)(UpdateProject);
